sys_script_include.script:
var EmployeeDetails = Class.create();
EmployeeDetails.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getDetails: function() {
		var employee = this.getParameter('sysparm_employee');
		var employeeGR = new GlideRecord('x_1045591_corpor_0_employee');
		employeeGR.get(employee);
		employeeGR.query();
		if(employeeGR.next()){
			return employeeGR.project_account.getDisplayValue();
		}
    },
    type: 'EmployeeDetails'
});
